    /**
     * Makes request to the server and gets BaUnit.
     */
    private void makeRequest() {
        if (JOptionPane.showConfirmDialog(this,
                "Your account will be charged for this request. Do you want to continue?",
                "Account charge", JOptionPane.YES_NO_OPTION) != JOptionPane.YES_OPTION) {
            return;
        }

        if (txtNameFirstPart.getText().isEmpty() || txtNameLastPart.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Fill in both fields.");
            return;
        }

        SolaTask t = new SolaTask<Boolean, Boolean>() {

            @Override
            protected Boolean doTask() {
                setMessage("Requesting property object");
                BaUnitBean baUnit = RequestBean.makeRequest(txtNameFirstPart.getText(),
                        txtNameLastPart.getText());

                if (baUnit == null) {
                    return null;
                }

                setMessage("Opening property form");
                PropertyForm form = new PropertyForm(baUnit);
                getMainContentPanel().addPanel(form, "PROPERTY_FORM", true);
                return true;
            }

            @Override
            protected void taskDone() {
                if (get() == null) {
                    JOptionPane.showMessageDialog(null, "Property object not found.");
                }
            }
        };

        TaskManager.getInstance().runTask(t);
    }

